mapper.select:
SELECT 

FROM crm_customer a

WHERE 1=1 

	AND a.id = #{id} 


	AND a.membercard = #{membercard}

SELECT 

FROM crm_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.customer_name LIKE 
			'%'||#{customerName}||'%'
			'%'+#{customerName}+'%'
			concat('%',#{customerName},'%')


		AND a.phone = #{phone}


		AND a.sex = #{sex}


		AND a.membercard = #{membercard}


		AND a.customer_no = #{customerNo}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM crm_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE crm_customer SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
