mapper.select:
SELECT

FROM f_payment a

WHERE a.id = #{id}

SELECT
FROM f_payment a
a.del_flag = #{DEL_FLAG_NORMAL}
AND c.membercard LIKE '%'||#{memberCard}||'%' '%'+#{memberCard}+'%' concat('%',#{memberCard},'%')
AND datediff(day, a.payment_time, #{paymentTime})=0 to_days(payment_time)=to_days(#{paymentTime})
AND a.payment_type = #{paymentType}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM f_payment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE f_payment SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
