mapper.select:
SELECT 

FROM oa_attendance a

WHERE a.id = #{id}

SELECT
FROM oa_attendance a
a.del_flag = #{DEL_FLAG_NORMAL}
AND b.customer_name LIKE '%'||#{customerName}||'%' '%'+#{customerName}+'%' concat('%',#{customerName},'%')
AND datediff(day, a.attendance_time, #{attendanceTime})=0 to_days(attendance_time)=to_days(#{attendanceTime})
AND c.reservation_number LIKE '%'||#{reservationNumber}||'%' '%'+#{reservationNumber}+'%' concat('%',#{reservationNumber},'%')
AND a.status = #{status}
AND b.member_card_number = #{memberCard}
ORDER BY ${page.orderBy}
ORDER BY a.attendance_time ASC

SELECT 

FROM oa_attendance a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_attendance SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
